// App.js (Updated with Enhancements)
import React, { useState, useCallback } from 'react';
import MarsRoverPhotos from './components/MarsRoverPhotos';
import Apod from './components/Apod';
import RoverManifest from './components/RoverManifest';
import './App.css';

const App = () => {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = useCallback(() => {
    setShowMenu((prev) => !prev);
  }, []);

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Explore Mars & The Universe</h1>
        <p>Your gateway to Mars Rover photos and the Astronomy Picture of the Day (APOD)!</p>
        <button className="menu-toggle" onClick={toggleMenu}>â˜° Menu</button>
      </header>

      {showMenu && (
        <nav className="mobile-menu">
          <ul>
            <li><a href="#mars">Mars Rover</a></li>
            <li><a href="#apod">APOD</a></li>
            <li><a href="#manifest">Rover Manifest</a></li>
          </ul>
        </nav>
      )}

      <section className="content-section">
        <div className="mars-rover-section" id="mars">
          <h2>Mars Rover Photos</h2>
          <MarsRoverPhotos />
        </div>

        <div className="apod-section" id="apod">
          <h2>Astronomy Picture of the Day</h2>
          <Apod />
        </div>

        <div className="manifest-section" id="manifest">
          <h2>Mars Rover Manifest</h2>
          <RoverManifest />
        </div>
      </section>

      <footer className="app-footer">
        <p>Powered by NASA APIs</p>
      </footer>
    </div>
  );
};

export default App;
