import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MarsRoverPhotos = () => {
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);

  const fetchPhotos = async (page) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        `http://localhost:5000/mars-rover-photos?rover=curiosity&camera=navcam&sol=1000&page=${page}`
      );
      setPhotos((prevPhotos) => [...prevPhotos, ...response.data.photos]);
    } catch (error) {
      setError('Error fetching Mars Rover photos');
      console.error('Error fetching Mars Rover photos:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPhotos(page);
  }, [page]);

  return (
    <div>
      <h1>Mars Rover Photos</h1>

      {/* Loading spinner */}
      {loading && <p>Loading...</p>}

      {/* Error message */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {photos.map((photo) => (
          <img
            key={photo.id}
            src={photo.img_src}
            alt={`Mars Rover ${photo.id}`}
            style={{
              width: '200px',
              height: 'auto',
              margin: '10px',
              borderRadius: '8px',
              transition: 'transform 0.3s ease',
            }}
            onClick={() => window.open(photo.img_src, '_blank')}
            onMouseOver={(e) => (e.target.style.transform = 'scale(1.05)')}
            onMouseOut={(e) => (e.target.style.transform = 'scale(1)')}
          />
        ))}
      </div>

      {/* Load More Button */}
      {!loading && (
        <button onClick={() => setPage((prevPage) => prevPage + 1)} style={{ marginTop: '20px', padding: '10px 20px' }}>
          Load More Photos
        </button>
      )}
    </div>
  );
};

export default MarsRoverPhotos;
