import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MarsRoverPhotos.css'; // Importing the CSS file

const MarsRoverPhotos = () => {
  const [photos, setPhotos] = useState([]);
  const [rover, setRover] = useState('curiosity');
  const [camera, setCamera] = useState('navcam');
  const [sol, setSol] = useState(1000);
  const [page, setPage] = useState(1);

  // Effect hook to fetch data whenever filters change
  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const url = `http://localhost:5000/mars-rover-photos?rover=${rover}&camera=${camera}&sol=${sol}&page=${page}`;
        console.log('Fetching data from:', url); // Debugging log for URL
        const response = await axios.get(url);
        console.log('Photos response:', response.data.photos); // Log to see the API response
        setPhotos(response.data.photos);
      } catch (error) {
        console.error('Error fetching Mars Rover photos:', error);
      }
    };

    fetchPhotos();
  }, [rover, camera, sol, page]); // Dependencies to re-fetch when filters change

  // Handlers for filter changes
  const handleRoverChange = (e) => {
    setRover(e.target.value);
    console.log('Rover changed to:', e.target.value); // Debugging line
  };

  const handleCameraChange = (e) => {
    setCamera(e.target.value);
    console.log('Camera changed to:', e.target.value); // Debugging line
  };

  const handleSolChange = (e) => {
    setSol(e.target.value);
    console.log('Sol changed to:', e.target.value); // Debugging line
  };

  const handlePageChange = (newPage) => {
    setPage(newPage);
    console.log('Page changed to:', newPage); // Debugging line
  };

  return (
    <div className="mars-rover-container">
      <h1>Mars Rover Photos</h1>

      {/* Filters */}
      <div className="filters">
        <label>
          Rover:
          <select value={rover} onChange={handleRoverChange}>
            <option value="curiosity">Curiosity</option>
            <option value="opportunity">Opportunity</option>
            <option value="spirit">Spirit</option>
          </select>
        </label>

        <label>
          Camera:
          <select value={camera} onChange={handleCameraChange}>
            <option value="navcam">Navcam</option>
            <option value="mastcam">Mastcam</option>
            <option value="fhaz">FHAZ</option>
            {/* Add more camera options as needed */}
          </select>
        </label>

        <label>
          Sol:
          <input type="number" value={sol} onChange={handleSolChange} />
        </label>

        <label>
          Page:
          <button onClick={() => handlePageChange(page - 1)} disabled={page <= 1}>Previous</button>
          <span>{page}</span>
          <button onClick={() => handlePageChange(page + 1)}>Next</button>
        </label>
      </div>

      {/* Display Photos */}
      <div className="photos">
        {photos.length > 0 ? (
          photos.map((photo) => (
            <img
              key={photo.id}
              src={photo.img_src}
              alt={`Mars Rover ${photo.id}`}
              className="photo"
            />
          ))
        ) : (
          <p>No photos available for the selected filters.</p>
        )}
      </div>
    </div>
  );
};

export default MarsRoverPhotos;
