import React, { useEffect, useState } from 'react';
import axios from 'axios';
import App from '../App';


const MarsRoverPhotos = () => {
  const [photos, setPhotos] = useState([]);
  const [rover, setRover] = useState('curiosity');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPhotos = async () => {
      setLoading(true);
      setError('');
      try {
        const response = await axios.get(
          `http://localhost:5000/mars-rover-photos?rover=${rover}&camera=navcam&sol=1000`
        );
        
        if (response.data.photos.length === 0) {
          setError(`No photos available for rover ${rover}.`);
        } else {
          setPhotos(response.data.photos);
        }
      } catch (error) {
        setError('Error fetching Mars Rover photos');
      } finally {
        setLoading(false);
      }
    };
    fetchPhotos();
  }, [rover]);

  const handleRoverChange = (event) => {
    setRover(event.target.value);
  };

  return (
    <div className="mars-rover-container">
      <h1>Mars Rover Photos</h1>

      <select value={rover} onChange={handleRoverChange}>
        <option value="curiosity">Curiosity</option>
        <option value="opportunity">Opportunity</option>
        <option value="spirit">Spirit</option>
      </select>

      {loading && <p className="loading">Loading...</p>}

      {error && <p className="error-message">{error}</p>}

      <div className="photo-gallery">
        {photos.length > 0 ? (
          photos.map((photo) => (
            <img
              key={photo.id}
              src={photo.img_src}
              alt={`Mars Rover ${photo.id}`}
              className="rover-image"
            />
          ))
        ) : (
          <p>No images to display for this rover.</p>
        )}
      </div>
    </div>
  );
};

export default MarsRoverPhotos;
